import React, { useState, useEffect } from 'react';
import firebase from "../commonjs/firebase.js";
import reqMessage from "../reqMessage";



var database = firebase.database();
var starCountRef =database.ref('Sensors/request/message');


function sendfirebase() {
    const reqmsg = new reqMessage("IF9999");
    console.log("send firebase");
    starCountRef.set(JSON.stringify(reqmsg));
    
  }

  

  

const About = () => {

    const [sensorvalue, sensorupdate] = useState(1);

    

    
  useEffect(() => {
    
    starCountRef.on('value', (snapshot) => {
        const data = snapshot.val();
        sensorupdate(data) ;
        console.log(data);
    });

  }, []);


    return(
        <div>
            <h2>about Page</h2>
            <div key="sdaff">
            <h3 >sensor : {sensorvalue} </h3>

            <div className="out_button">
             <button className="button_on" onClick={() => sendfirebase()}>메시지전달</button> 
            </div>

            </div>
        </div>
    );
}

export default About;
