import fb from "./../serviceKey/firebase_config.js";
import { notify } from "./notify.js";

firebase.initializeApp(fb.getfirebaseConfig());


async function authStateCheck() {
    var chkUser;
    return new Promise((resolve, reject) => {
        firebase.auth().onAuthStateChanged(function (user) {
            chkUser = user;
            resolve(chkUser);
        })
    })
}

function signOut() {
    firebase.auth().signOut().then(() => {
        notify("로그아웃 되었습니다.");
        location.reload();
    }).catch((error) => {
        notify("로그아웃 에러발생:\n" + error);
    });
}

export {
    authStateCheck,
    signOut,
};
