import React, { Component,useState, useEffect } from "react";
import Sensordisplay from "../sensordisplay";
import Outputdevicedisplay from "../outputdevicedisplay";
import IndoorFarmAPI from "../indoorfarmapi";




const Dashboard = () => {

    const [msensorsarray, setUpdatesensor] = useState([]);
    const [moutdevarray, setUpdatedevice] = useState([]);

    useEffect(() => {
      let interval = null;
  
      interval = setInterval(() => {

        

        IndoorFarmAPI.getmultiple(true,true,false).then((mrsp) => {
            setUpdatesensor(mrsp.Sensors);
            setUpdatedevice(mrsp.Outputs);

        });

      }, 1000);
  
      return () => clearInterval(interval);
    }, []);


    return(
        <div>
            <h2>dashboard Page</h2>
            <div className="sensorbocck">{Sensordisplay(msensorsarray,true)}</div>
            <div className="outputblock">{Outputdevicedisplay(moutdevarray,true)}</div>

        </div>
    )
}

export default Dashboard;
