const initState = {
    Islogin : false,
    value: 0
    
}

function mainReducer(state = initState, actions) 
{
    switch(actions.type){
        case SETVALUE:
            return state = {
                ...state,
                value  : actions.intvalue
            };
        case SETLOGIN:
            return state = {
                ...state,
                Islogin : actions.islogin
            };
        default:
            return state;
    }
}

export default mainReducer;
