import React, { useState, useEffect } from "react";
import Sensordisplay from "../sensordisplay";
import IndoorFarmAPI from "../indoorfarmapi";

const Settingpage = () => {
  let rlist = [];
  const [relaylist, selectedrelay] = useState([]);

  for (let i = 1; i < 25; i++) {
    rlist.push("릴레이 " + i);
  }

  function devicecheckbox(relayname) {
    return (
      <ui>
        <input type="checkbox" key={relayname} name="relaycheck" defaultChecked={false} id={relayname} /> {relayname}
      </ui>
    );
  }

  useEffect(() => {
    let interval = null;
    selectedrelay(rlist);
  }, []);

  return (
    <div>
      <div className="dev_name">출력장치 선택</div>
      <div className="dev_select">{relaylist.map((localState, index) => devicecheckbox(localState))}</div>
    </div>
  );
};

export default Settingpage;
