import { SETVALUE, SETLOGIN } from './mainAction';

const initState = {
    loginrole : "logout",
    value: 0
    
}
 
function mainReducer(state = initState, actions) 
{
    switch(actions.type){
        case SETVALUE:
            return state = {
                ...state,
                value  : actions.intvalue
            };
        case SETLOGIN:
            return state = {
                ...state,
                loginrole : actions.loginrole
            };
        default:
            return state;
    }
}

export default mainReducer;
