import React, { Component, useState, useEffect } from "react";
import AutoControlconfig from "../commonjs/autocontrolconfig";
import IndoorFarmAPI from "../indoorfarmapi";

const Autocontrolpage = () => {
  const [mAutolist, setUpdate] = useState([]);
  const [mSelecteditem, setupslected] = useState(null);


  useEffect(() => {
    IndoorFarmAPI.getautocontrols().then((mlist) => {
      setUpdate(mlist);
    });
  }, []);

  function setupSave(mdata) {
    console.log("setupSave : " + mdata.mConfig.name);
   // setupslected(mdata);
  }



  function autocontroleditbox(mydata) {

    let newcfg=new AutoControlconfig();

    if(mydata == null)
    {

      return (<div></div>);
    }

    return (

          
         <div className="autosetupbox">
        <ul>{mydata.mConfig.name}</ul>
        <input type="text" value = {mydata.mConfig.name} onChange={(e)=> newcfg.name=e.target.value}> </input>
        <ul>sdkjsfalkeee</ul>
        <button  onClick={() => setupSave(mydata)} id="editcheck" >  저장</button>

        <button  onClick={() => setupslected(null)} id="editcheck" >  취소</button>

        </div>
      
      
    
    );
  }
  function autocontrolbox(mydata) {
    
    //<input type="checkbox" onClick={() => autoedit(mydata)} id="editcheck" />
    return (
      <div className="autocontrolbox">
        <ui> ... ...............</ui>
        <ui>{mydata.mConfig.name}</ui>
        <span> </span>
        <ui>{mydata.mConfig.enabled == true ? "작동중" : "정지됨"}</ui>
        <button  onClick={() => setupslected(mydata)} id="editcheck" >  수정</button>
       
        
      </div>
    );
  }

  return (
    <div>
      <h4>Autocontrol Page</h4>
      <div className="controltop">
        정렬 :
        <select name="pets" id="pet-select">
          <option value="1">시간순서</option>
          <option value="2">이름순서</option>
          <option value="3">카테고리</option>
        </select>
        <button>+ 추가</button>
      </div>
      <div className="autocontroltable">
        {autocontroleditbox(mSelecteditem)}

        {mAutolist.map((localState, index) => autocontrolbox(localState))}
      </div>
    </div>
  );
};

export default Autocontrolpage;
