import "./App.css";

import React, {Component,  useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';



import  Dashboard from './pages/dashboard';
import  Sensorpage from './pages/sensorpage';
import  Devicepage from './pages/devicepage';
import  Autocontrolpage from './pages/autocontrolpage';
import  About from './pages/about';



function App() {
  const [hwchannel, sethwchannel] = useState(0);
  function handleChange(e) {
    sethwchannel(e.target.value);
    console.log(hwchannel);
  }

  return (
    <div className="App">

      <Router>
      <div className="kd">
        <div className="indoor">

          <div className="left">
            <div className="name"><img src="./image/kdg.png" className="namelogo"/> SFC-XXX</div>
          <div className="menu">
              <div className="content"><img src="./image/s_dash.png" className="contentimg"/>  DASH BOARD</div>
              <div className="content"><img src="./image/s_sen.png" className="contentimg"/>  SENSOR</div>
              <div className="content"><img src="./image/s_dev.png" className="contentimg"/>  DEVICE</div>
              <div className="content"><img src="./image/s_aut.png" className="contentimg"/>  AUTOCONTROL</div>
              <div className="content"><img src="./image/s_set.png" className="contentimg"/>  SETTING</div>
          </div>
          </div>


          <div  className="right">
          <Route path='/dashboard' component={Dashboard}/>
          <Route path='/devices' component={Devicepage}/>
          <Route path='/about' component={About}/>
          <Route path='/sensor' component={Sensorpage}/>
          <Route path='/autocontrol' component={Autocontrolpage}/>


          </div>

        </div>
        </div>

      </Router>


    </div>
  );
}

export default App;
