import { notify } from "./notify.js";

const ref = firebase.database().ref();

var now = new Date();

async function saveLog(u_Email) {
    var today = now.getFullYear() + "-" + (now.getMonth() + 1) + "-" + now.getDate();
    var time = now.getHours() + ":" + now.getMinutes() + ":" + now.getSeconds();
    var n_Time = today + " " + time;

    new Promise((resolve, reject) => {
        firebase.database().ref('Log/userLogin/' + n_Time).update({
            email: u_Email
        })
        resolve();
    });
}

async function loadDevce(uid) {
    var dataDevices = new Array();

    return new Promise((resolve, reject) => {
        ref.child("users").child(uid).child("devices").get().then((snapshot) => {
            if (snapshot.exists()) {
                for (const i of snapshot.val()) {
                    dataDevices.push([i.keyname, i.key]);
                }
                console.log(dataDevices);
                resolve(dataDevices);
            } else {
                resolve(null);
            }
        }).catch((error) => {
            notify("기기정보를 불러오는 도중 에러가 발생했습니다.\n 에러코드: " + error);
            resolve(null);
        });
    });
}

function logDevice(m_key, m_email) {
    var today = now.getFullYear() + "-" + (now.getMonth() + 1) + "-" + now.getDate();
    var time = now.getHours() + ":" + now.getMinutes() + ":" + now.getSeconds();
    var n_Time = today + " " + time;

    return new Promise((resolve, reject) => {
        firebase.database().ref('Devices/SIRRI/' + m_key + '/LOG/' + n_Time + '/').update({
            email: m_email
        });
        resolve();
    });
}

function popupGraph(deviceAddr_path) {
    return new Promise((resolve, reject) => {
        ref.child("Devices").child("SIRRI").child(deviceAddr_path[0]).child(deviceAddr_path[1]).child(deviceAddr_path[2]).child(deviceAddr_path[3]).child(deviceAddr_path[4]).child(deviceAddr_path[5]).child(deviceAddr_path[6]).child("INFO").get().then((snapshot) => {
            resolve(snapshot.val().graphUrl);
        }).catch(() => {
            resolve("not yet");
        });
    })
}

function popupRTVideo(deviceAddr_path) {
    return new Promise((resolve, reject) => {
        ref.child("Devices").child("SIRRI").child(deviceAddr_path[0]).child(deviceAddr_path[1]).child(deviceAddr_path[2]).child(deviceAddr_path[3]).child(deviceAddr_path[4]).child(deviceAddr_path[5]).child(deviceAddr_path[6]).child("INFO").get().then((snapshot) => {
            resolve(snapshot.val().cameraUrl);
        }).catch(() => {
            resolve("not yet");
        });
    });
}

async function deleteDevice(uid, m_name, m_email, d_num) {
    var dn = d_num;
    var devices;

    return new Promise((resolve, reject) => {
        ref.child("users").child(uid).child("devices").get().then((snapshot) => {
            if (snapshot.exists()) {
                var saveData = snapshot.val();
                var temp = snapshot.val().length;
                if (dn >= temp) {
                    resolve(null);
                } else if (dn < temp) {
                    delete saveData[dn];
                    devices = saveData.filter((element) => true);

                    new Promise((resolve2, reject2) => {
                        firebase.database().ref('users/' + uid).set({
                            userName: m_name,
                            email: m_email,
                            devices
                        });
                        resolve2();
                    });
                    resolve(uid);
                }
            } else {
                resolve(null);
            }
        });
    });

}

function registOrModify(uid, email, name, dNum, keyName, key) {
    firebase.database().ref('users/' + uid + '/').update({
        userName: name,
        email: email
    });

    var temp_key = key.replace(/ /g, "");
    new Promise((resolve, reject) =>{
        if (dNum == "") {
            ref.child("users").child(uid).child("devices").get().then((snapshot) => {
                if (snapshot.exists()) {
                    var dn = snapshot.val().length;
                    firebase.database().ref('users/' + uid + "/devices/" + dn + "/").update({
                        key: temp_key,
                        keyname: keyName
                    }, (error) => {
                        if (error) {
                            notify(error);
                        } else {
                            notify("디바이스 정보를 추가 했습니다.");
                        }
                      });
                } else {
                    firebase.database().ref('users/' + uid + "/devices/0/").update({
                        key: temp_key,
                        keyname: keyName
                    });
                }
                notify("디바이스 정보를 추가 했습니다.");
            });
        } else {
            ref.child("users").child(uid).child("devices").get().then((snapshot) => {
                if (snapshot.exists()) {
                    if (dNum >= snapshot.val().length) {
                        notify("수정할 디바이스를 찾을 수 없습니다. 확인 후 다시 시도해주십시오.");
                    } else {
                        var device = "/devices/" + dNum + "/";
                        firebase.database().ref('users/' + uid + device).update({
                            key: temp_key,
                            keyname: keyName
                        });
                        notify("디바이스 정보를 수정했습니다.");
                    }
                } else {
                    notify("디바이스 정보를 수정하는 도중 에러가 발생했습니다.");
                }
            });
        }
    })
}

export {
    ref,
    saveLog,
    loadDevce,
    logDevice,
    popupGraph,
    popupRTVideo,
    deleteDevice,
    registOrModify
}
