import React, { useState, useEffect } from 'react';
//import firebase from "../commonjs/firebase.js";
import reqMessage from "../reqMessage";

import KDCommon from "../commonjs/kdcommon";




var starresponse =database.ref('Sensors/response/message');


function sendfirebase() {

    let database = KDCommon.getFirebaseDB();
    let starCountRef =database.ref('Sensors/request/message');

    const reqmsg = new reqMessage("IF9999");
    console.log("send firebase");
    starCountRef.set(JSON.stringify(reqmsg));
    
  }

  

  

const About = () => {

    const [sensorvalue, sensorupdate] = useState(1);
    const [repvalue, reponseupdate] = useState(1);

    

    
  useEffect(() => {
    
    starCountRef.on('value', (snapshot) => {
        const data = snapshot.val();
        sensorupdate(data) ;
        console.log(data);
    });

    starresponse.on('value', (snapshot) => {
        const data = snapshot.val();
        reponseupdate(data) ;
        console.log(data);
    });


    


  }, []);


    return(
        <div>
            <h2>about Page</h2>
            <div key="sdaff">
            <h3 >request : {sensorvalue} </h3>

            <h3 >response : {repvalue} </h3>
            

            <div className="out_button">
             <button className="button_on" onClick={() => sendfirebase()}>메시지전달</button> 
            </div>

            </div>
        </div>
    );
}

export default About;
