import React, { useState, useEffect } from 'react';
import firebase from "firebase/app";
import "firebase/database";




var firebaseConfig = {
    apiKey: "AIzaSyAmiscjBV0mf4O4kdG_aK7T4J6Jd7RrJeE",
    authDomain: "indoorfarm-6f7b8.firebaseapp.com",
    databaseURL: "https://indoorfarm-6f7b8-default-rtdb.firebaseio.com/",
    projectId: "indoorfarm-6f7b8",
    storageBucket: "indoorfarm-6f7b8.appspot.com",
    messagingSenderId: "141091391091",
    appId: "1:141091391091:web:4f2005e37d6c8fbde24c06",
    measurementId: "G-8JM4DB8X77"
};
firebase.initializeApp(firebaseConfig);
var database = firebase.database();
var starCountRef =database.ref('Sensors/test');


function sendfirebase() {
  
    console.log("send firebase");
    starCountRef.set("haha");
    
    
  }

  

const About = () => {

    const [sensorvalue, sensorupdate] = useState(1);

    

    
  useEffect(() => {
    
    starCountRef.on('value', (snapshot) => {
        const data = snapshot.val();
        sensorupdate(data) ;
        console.log(data);
    });

  }, []);


    return(
        <div>
            <h2>about Page</h2>
            <div key="sdaff">
            <h3 >sensor : {sensorvalue} </h3>

            <div className="out_button">
             <button className="button_on" onClick={() => sendfirebase()}>메시지전달</button> 
            </div>

            </div>
        </div>
    );
}

export default About;
