const mapStateToProps = function (state) {
  return {
    Islogin: state.Islogin,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    onSetlogin: (value) => dispatch(actionSetlogin(value)),
  };
};

export default connect(mapStateToProps,mapDispatchToProps)(App);
